$uid..validate:
newData.hasChildren(['name', 'role']) && (
          // Se for ADMIN ou MASTER, pode fazer qualquer coisa
          (root.child('users').child(auth.uid).child('role').val() === 'admin' || root.child('users').child(auth.uid).child('role').val() === 'master') || 
          (
            // Se for um novo usuário (registro):
            (!data.exists() && newData.child('role').val() != 'admin' && newData.child('role').val() != 'master' && newData.child('status').val() === 'active') ||
            // Se for um usuário editando o próprio perfil: NÃO PODE mudar role nem status
            (data.exists() && newData.child('role').val() === data.child('role').val() && newData.child('status').val() === data.child('status').val())
          )
        )
